settings.custom_css:
.button--primary {
  position: relative;
  overflow: hidden;
  background: transparent;
  color: #fff;
}

.button--primary::before {
  content: "";
  position: absolute;
  top: 100%;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #3CAA35;
  transition: all 0.3s ease-in-out;
  z-index: -1;
}

.button--primary:hover::before {
  top: 0;
}
.button--primary {
background-color: rgba(255, 255, 255, 0.25);

}

