settings.custom_css:
.product__info {
  background: #fafafa;
  border-radius: 10px;
  padding: 30px;
  border: 4px solid #ededed;
  max-width: 450px;
}
.product-meta__title.heading.h3 {
  font-size: 150%;
}
.price--large {
  font-size: 110%;
}
.product__media-image-wrapper img {
  padding: 40px;
}
.product-tabs__trust-list {
  padding: 10px 0;
}
.select {
  padding-left: 15px;
}
.product-form__option-info {
  padding-left: 5px;
  font-weight: 500;
}
.product-form__inventory-wrapper {
  margin-top: 15px;
}
.icon-text {
  padding: 5px;
}
.inventory--high {
  font-weight: 500;
}
.input__field {
  border: 1.5px solid #dddddd;
}

settings.mobile_custom_css:
.product__info {
  background: #fafafa;
  border-radius: 0px;
  padding: 30px;
  border: 0px solid #ededed;
  max-width: 450px;
}
