settings.custom_css:
.product__info {
  background: #fafafa;
  border-radius: 10px;
  padding: 30px;
  border: 4px solid #ededed;
  max-width: 450px;
}
.product-meta__title.heading.h3 {
  font-size: 150%;
}
.price--large {
  font-size: 110%;
}
.product__media-image-wrapper img {
  padding: 40px;
}
.product-tabs__trust-list {
  padding: 10px 0;
}
.select {
  padding-left: 15px;
}
.product-form__option-info {
  padding-left: 5px;
  font-weight: 500;
}
.product-form__inventory-wrapper {
  margin-top: 15px;
}
.icon-text {
  padding: 5px;
}
.inventory--high {
  font-weight: 500;
}

settings.mobile_custom_css:
.product__info {
  background: #ffffff;
  border-radius: 0px;
  padding: 30px;
  border: 0px solid #ededed;
  max-width: 450px;
}

settings.custom_css:
.dsgn-pck__inner-content {
   width: min(800px, 80%);
}

.dsgn-pck__image img {
  border-radius: 8px;
}

.dsgn-pck__image {
  padding-top: 25px;
}

.dsgn-pck__background {
  max-width: 1250px;
  margin: auto;
  border-radius: 10px;
  padding-left: 0px;
}

settings.mobile_custom_css:
.dsgn-pck__text {
  width: 280px;
  margin-left: -10px;
}

.dsgn-pck__heading {
  width: 300px;
  margin-left: -10px;
}

.dsgn-pck__background {
  margin-left: 20px;
  margin-right: 20px;
  border-radius: 10px;
  padding-left: 0px;
  padding-right: 0px;
}

.dsgn-pck__image {
  padding-top: 0px;
}
